import styled from "styled-components";
import ItemsDisplay from "../shared/ItemsDisplay";
import {useState, useEffect} from "react"
import Item from "../shared/Item";
import Banner from "../shared/Banner";

const Products = () => {
   //set state to render the products
  const [itemProducts, setItemProducts] =useState(null)
  //set the error state
  const [error, setError] = useState(null)
//fetch all products for Item to render "/api/get-items"
  useEffect(()=>{
fetch("/api/get-items")
.then(res=> res.json())
.then(data=> {
  console.log(data)
  setItemProducts(data.data)
})
.catch(err=> setError(err))
  }, [])
  const array = [];

  //catching the erros
if(error) {
  return(
    <Wrapper>
        Error!!!! 
    </Wrapper>
)
}

  return (

    <Wrapper>
    {itemProducts &&
    <>
      <Banner />
      <Section>
        <h2>- All Products -</h2>
      <ItemsDisplay array={itemProducts} />
      </Section>
      </>
}
    </Wrapper>

  );
};

const Wrapper = styled.div`
  font-size: 20px;
  padding-top: 50px;
  width: 90%;
  margin: auto;
`;
const Section = styled.div`
  padding-top: 40px;

  h2 {
    text-align: center;
  }
`;

const ItemWrapper = styled.div`
  width: 90%;
  padding-top: 20px;
  margin: auto;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
  gap: 15px;

  div {
    border: var(--border);
    height: 15vw;
  }
`;

export default Products;
