import styled from "styled-components";
import ItemsDisplay from "../shared/ItemsDisplay";
import { useEffect, useState } from "react";


const Brands = () => {
  //set state to render the products
  const [error, setError] = useState(null);
  //set the error state
  const [itemProducts, setItemProducts] =useState(null);
  // sets the brand name
  const [brandName, setBrandName] = useState(null);
  const array = [];
  const urlPath = window.location.pathname.split('/');
  const brandId = urlPath[2];
  //sets the array to the brand's item 
  const displayProducts = () => {
    setItemProducts(array);
  }
//fetch all products for Item to render "/api/get-items"
  useEffect(() => {
    const getItems = () => {
      fetch("/api/get-items")
      .then((res) => res.json())
      .then((data) => {
        data.data.map((product) => {
          if(product.companyId == brandId){
            const item = {
              _id: product._id,
              name: product.name,
              imageSrc: product.imageSrc,
              price: product.price
            }
            array.push(item)
          }
        })
      })
      .catch((err) => setError(err))
      .finally(() => displayProducts());
    }
    const getBrandName = () => {
      fetch("/api/get-companies")
        .then((res) => res.json())
        .then((data) => {
          data.data.map((company) => {
            if (company._id == brandId){
              setcompany.name;
            }
          });
        })
        .catch((err) => setError(err))
    };
    getItems();
    getBrandName();
  }, []);

  if (error) {
    return <Wrapper>Error!!!!</Wrapper>;
  }
  return itemProducts?(
    <Wrapper>
      <Banner>
        This will be the banner for ProductsPage and brandsPage...
      </Banner>
      <Section>
        <h2>{brandName}</h2>
        <ItemWrapper>
          <ItemsDisplay array={itemProducts} />
        </ItemWrapper>
      </Section>
    </Wrapper>
  ):(
    <Wrapper>
      loading...
    </Wrapper>
  )
};

const Wrapper = styled.div`
  font-size: 20px;
  padding-top: 50px;
  width: 90%;
  margin: auto;
  max-width: var(--max-page-width);
`;
const Section = styled.div`
  padding-top: 40px;
  max-width: var(--max-content-widt);

  h2 {
    text-align: left;
    margin: auto;
  }
`;
const Banner = styled.div`
  height: 10vw;
  border: var(--border);
  margin: auto;
`;

const ItemWrapper = styled.div`
  width: 90%;
`;

export default Brands;
