import styled from "styled-components";
import { Link } from "react-router-dom";
import { useState } from "react";

const Header = () => {
    const [typedValue, setTypedValue] = useState('')

    // gets the value of what the user is typing in the search bar 
    const getValue = (event) => {
        setTypedValue(event.target.value);
    }
    return(
        <Wrapper>
            <Top>
                <h1>ADD LOGO</h1>
                <input type="text" placeholder="Search (icon)" value={typedValue} onChange={getValue}/>
                <div className="topLinkDiv">
                    <TopLinks className="signin" to='/signin'>Sign in</TopLinks>
                    <TopLinks to='/cart'>Add icon</TopLinks>
                    <TopLinks to='/wishlist'>Add icon</TopLinks>
                </div>
            </Top>
            <Nav>
                <NavLinks to="/" >Home</NavLinks>
                //T
                <NavLinks to="/">Category 1</NavLinks>
                <NavLinks to="/">Category 2</NavLinks>
                <NavLinks to="/">Category 3</NavLinks>
                <NavLinks to="/">Category 4</NavLinks>
                <NavLinks to='/brands'>Brands</NavLinks>
            </Nav>
        </Wrapper>
    );
}

const Wrapper = styled.div`
`;
const Top = styled.div`
    margin-top: 30px;
    padding-left: 40px;
    width: 100%;
    height: 80px;
    align-items: baseline;
    display: grid;
    grid-template-columns: 0.5fr .8fr 1fr ;
    gap: 40px;
    font-size: 20px;

    input{
        height: 35px;
        padding-left: 15px;
        font-size: 20px;
        border-radius: var(--border-radius);
        border: var(--border);
        margin-right: 50px;
    }
    input:focus{
        outline: none;
    }

    .topLinkDiv{
        //width: 40%;
        margin-left: 15%;
        padding-left: 10px;
        display: grid;
        grid-template-columns: 1fr 1fr 1fr ;
        gap: 30px;
    }
    .signin{
        border-right: var(--border);
        padding-left: 15px;
    }
`;
const TopLinks = styled(Link)`
    color: var(--primary-colour);
    text-decoration: none;

`;
const Nav = styled.div`
    padding-top: 20px;
    width: 90%;
    height: 50px;
    margin: auto;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr 1fr 1fr;
    border-top: var(--border);
`;
const NavLinks = styled(Link)`
    color: var(--primary-colour);
    text-decoration: none;
`;

export default Header;
