import styled from "styled-components";
import { Link } from "react-router-dom";

const Footer = () => {
    return(
        <Wrapper>
            <TopFooter>
                <div className="col1">
                    <p>About</p>
                    <p>Products</p>
                    <p>Contact Us</p>
                    <p>FAQ</p>
                    <p>Careers</p>
                </div>
                <div className="col2">
                    <p>My Account</p>
                    <p>Edit Information</p>
                    <p>Orders</p>
                    <p>Returns & Exchages</p>

                </div>
                <div className="col3">
                    <p>Sign up for Newsletters: </p>
                    <form>
                        <input type="text" placeholder="Email:" />
                        <button type="submit">Submit</button>
                    </form>

                </div>
            </TopFooter>
            <BottomFooter>
                <div className="other">
                    <p className="copyright"><span>&copy;</span> 2022 Copyright. All rights reserved.</p>
                    <p>Privacy |</p>
                    <p> Terms |</p>
                    <p> Sitemap </p>
                </div>
                <div className="socials">
                    <div className="box"></div>
                    <div className="box"></div>
                    <div className="box"></div>
                    <div className="box"></div>
                </div>
            </BottomFooter>

        </Wrapper>
    );
}
const Wrapper = styled.div`
    padding-top: 3vw;
    width: 90%;
    margin: auto;
    font-size: 1.2vw;

`;
const TopFooter = styled.div`

    border-top: var(--border);
    padding-top: 3vw;
    height: 10vw;
    display: grid;
    grid-template-columns: 1fr 1fr 2fr;
    .col3{
        font-size: 1.5vw;
        padding-left: 5vw;
    }
    button{
        margin-left: 1vw;
    }
    input{
        height: 2vw;
        padding-left: 1vw;
        font-size: 1.4vw;
        border-radius: var(--border-radius);
        border: var(--border);
        margin-top: 1vw;
    }
    input:focus{
        outline: none;
    }
`;
const BottomFooter = styled.div`
    border-top: var(--border);
    width: 5vw;
    display: flex;

    .other{
        display: flex;
    }
    p{
        padding-right: 1vw;
    }

    .socials{
        display: flex;
    }
    .box{
        border: var(--border);
        width: 2vw;
        height: 2vw;
    }
`;
export default Footer;
