import { useEffect, useState } from "react";
import DisplayError from "./shared/DisplayError";
import styled from "styled-components";
import { Link } from "react-router-dom";

const SearchBar = () => {
    const [suggestions, setSuggestions] = useState();
    const [typedValue, setTypedValue] = useState("");
    const [itemsArr, setItemArr] = useState();
    const [error, setError] = useState();
    const  array = [];

    useEffect(() => {
        const getItems = () => {
            fetch("/api/get-items")
            .then((res) => res.json())
            .then((data) => {
                data.data.map((product) => {
                    const item = {
                        _id: product._id,
                        name: product.name,
                    };
                    array.push(item);
                });
            })
            .catch((err) => setError(err))
            .finally(() => setItemArr(array));
        }
        getItems();
    }, [])

    const displayMatches = (matches) => {
        matches.map(match => {
            if (suggestions.length > 7){
                const element = <Suggestion to={`/products/${match._id}`}>{match.name}</Suggestion>
                setSuggestions((suggestions) => [...suggestions, element]);
            }
        })
    }

    const searchMatches = () => {
        if (itemsArr) {
            const matches = []
            itemsArr.map(item => {
                const name = item.name.toLowerCase();
                if ((name.search(typedValue)) !== -1){
                    matches.push(item)
                }
            })
            displayMatches(matches);
        }
    }

    useEffect(() => {
        if (typedValue.length >= 1){
            searchMatches();
        }
    }, [typedValue])

    return(
        <input
            type="text"
            placeholder="🔍 Search"
            value={typedValue}
            onChange={getValue}
        />
    )
    if (error) {
        return (<Wrapper>
            <DisplayError/>
            </Wrapper>);
        }
    return suggestions?(
        <Wrapper>

        </Wrapper>
    ):(
        <NoSuggestion>

        </NoSuggestion>
    )
}
const Wrapper = styled.div`
    display: none;
`;

const Suggestion = styled(Link)`

`;

const NoSuggestion = styled.div`

`;
export default SearchBar;
