import styled from "styled-components";

// This Fun is for user's address update
// Also for "sign out" and display the user's orders
const Account = () => {
  return (
    <Wrapper>
      <Section>
        <LeftSection>
          <Icon>Icon here</Icon>
          <Button type="">Sign out</Button>
        </LeftSection>

        <RightSection>
          <form>
            <Title>My Name: </Title>
            <Title>Edit Your Address:</Title>

            <Label for="address">Address</Label>
            <Input type="text" placeholder="Your apt/unit and street address" />

            <Label for="city">City</Label>
            <Input type="text" placeholder="Your city" />

            <Label for="Province">Province/Territory</Label>
            <Input type="text" placeholder="Your province/territory" />

            <Label for="Postal code">Postal code</Label>
            <Input type="text" placeholder="Your postal code" />

            <SubmitButton type="submit">Sumit</SubmitButton>
          </form>

          <OrdersSection>
            <Title>My Orders :</Title>
            <Order>
              <OrderId>Order id:</OrderId>

              <Hr />

              <List>Address: // user's address</List>
              <List>Total Price:</List>
              <List>Items in order:</List>
              <List>
                Item name and <span>Qty: // quantity</span>
              </List>
            </Order>
          </OrdersSection>
        </RightSection>
      </Section>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  font-size: 20px;
  line-height: 1.5rem;
  width: 90%;
  margin: auto;
  max-width: var(--max-page-width);
  padding: 100px 0;
`;

const Section = styled.div`
  display: flex;
  justify-content: center;
  align-items: flex-start;
  max-width: var(--max-content-width);
  margin: auto;
`;

const LeftSection = styled.div`
  width: 10%;
  width: 120px;
  height: 120px;
  margin-right: 50px;
`;

const Button = styled.button`
  background-color: white;
  border: var(--border);
  border-radius: 8px;
  box-sizing: border-box;
  cursor: pointer;
  display: inline-block;
  font-size: 1rem;
  font-weight: 600;
  line-height: 20px;
  margin-top: 20px;
  margin-right: 20px;
  padding: 13px 23px;
  text-align: center;
  transition: box-shadow 0.2s, -ms-transform 0.1s, -webkit-transform 0.1s,
    transform 0.1s;
  width: 120px;

  &:focus-visible {
    box-shadow: #222222 0 0 0 2px, white 0 0 0 4px;
    transition: box-shadow 0.2s;
  }

  &:active {
    background-color: white;
    border-color: black;
    transform: scale(0.96);
  }
`;

const Icon = styled.div`
  width: 10%;
  width: 120px;
  height: 120px;
  border: var(--border);
  margin: auto;
`;

const RightSection = styled.div`
  width: 90%;
`;

const Label = styled.label`
  font-size: 1.2rem;
`;

const Input = styled.input`
  width: 100%;
  padding: 10px;
  font-size: 1rem;
  border-radius: 8px;
  box-sizing: border-box;
  border: var(--border);
  margin-bottom: 20px;
`;

const Title = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 20px;
  color: var(--primary-colour);
  font-weight: bolder;
`;

const OrdersSection = styled.div`
  margin-top: 30px;
`;

const OrderId = styled.p`
  font-size: 1.2rem;
  font-weight: bold;
`;

const List = styled.p`
  font-size: 1.2rem;
`;

const Hr = styled.hr`
  border-bottom: var(--border) solid black;
`;

const SubmitButton = styled.button`
  background-color: white;
  border: var(--border);
  border-radius: 8px;
  box-sizing: border-box;
  cursor: pointer;
  display: inline-block;
  font-size: 1rem;
  font-weight: 600;
  line-height: 20px;
  margin-top: 10px;
  margin-right: 20px;
  padding: 13px 23px;
  text-align: center;
  transition: box-shadow 0.2s, -ms-transform 0.1s, -webkit-transform 0.1s,
    transform 0.1s;
  width: 100%;

  &:focus-visible {
    box-shadow: #222222 0 0 0 2px, white 0 0 0 4px;
    transition: box-shadow 0.2s;
  }

  &:active {
    background-color: white;
    border-color: black;
    transform: scale(0.96);
  }
`;

const Order = styled.div`
  box-sizing: border-box;
  border: var(--border);
  border-radius: 8px;
  padding: 10px;
  margin-bottom: 10px;
`;

export default Account;
