import styled from "styled-components";
import { useParams } from "react-router-dom";

const ProductDetails = () => {
  // fetch data to insert details of item
  const { item } = useParams();

  return (
    <Wrapper>
      <ImgSection>
        <img src="" alt="" />
      </ImgSection>

      <DetailSection>
        <div>
          <ProductName>Product Name</ProductName>

          <Description>
            description: Mollit esse duis sunt Lorem laborum commodo sint
            proident mollit magna.
          </Description>

          <Price>Price</Price>
        </div>

        <ButtonSection>
          <button>Add to cart</button>
          <button>heart icon</button>
        </ButtonSection>
      </DetailSection>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: center;
  font-size: 20px;
  line-height: 1.5rem;
  padding-top: 50px;
  width: 90%;
  margin: auto;
`;

const ImgSection = styled.section`
  border: var(--border);
  height: 30vw;
  width: 30vw;
  margin-right: 10%;
`;

const DetailSection = styled.section`
  margin-top: 2%;
  width: 30vw;
`;

const ProductName = styled.h2`
  font-size: 1.5rem;
`;

const Description = styled.p`
  margin-top: 8%;
  font-size: 1rem;
`;

const Price = styled.p`
  margin-top: 8%;
  font-size: 1.3rem;
`;

const ButtonSection = styled.section`
  display: flex;
  margin-top: 8%;
`;

export default ProductDetails;
