import styled from "styled-components";
import { useState, useEffect } from "react";

import Banner from "../shared/Banner";
import ItemsDisplay from "../shared/ItemsDisplay";

const Products = () => {
  //set state to render the products
  const [itemProducts, setItemProducts] = useState(null);
  //set the error state
  const [error, setError] = useState(null);
  //fetch all products for Item to render "/api/get-items"
  useEffect(() => {
    fetch("/api/get-items")
      .then((res) => res.json())
      .then((data) => {
        console.log(data);
        setItemProducts(data.data);
      })
      .catch((err) => setError(err));
  }, []);
  const array = [];

  //catching the erros
  if (error) {
    return <Wrapper>Error!!!!</Wrapper>;
  }

  return (
    <Wrapper>
      {itemProducts ? (
        <>
          <Banner />
          <Section>
            <h2>- All Products -</h2>

            <ItemsDisplay array={itemProducts} />
          </Section>
        </>
      ) : (
        <p>Loading...</p>
      )}
    </Wrapper>
  );
};

const Wrapper = styled.div`
  font-size: 20px;
  padding-top: 50px;
  width: 90%;
  margin: auto;
  max-width: var(--max-page-width);
`;
const Section = styled.div`
  padding-top: 40px;

  h2 {
    font-weight: bolder;
    text-align: center;
  }
`;

export default Products;
