  return (
    <Wrapper>
      <Banner />
      <Section>
        <h2>Brand Name</h2>
        <h2>Brand url</h2>
        <h2>Brand country</h2>

        <ItemWrapper>
          <Item array={array} />
        </ItemWrapper>
      </Section>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  font-size: 20px;
  padding-top: 50px;
  width: 90%;
  margin: auto;
  max-width: var(--max-page-width);
`;
const Section = styled.div`
  padding-top: 40px;
  max-width: var(--max-content-widt);

  h2 {
    text-align: left;
    width: 90%;
    margin: auto;
  }
`;

const ItemWrapper = styled.div`
  width: 90%;
  padding-top: 20px;
  margin: auto;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
  gap: 15px;

  div {
    border: var(--border);
    height: 15vw;
  }
`;

export default Brands;
