import styled from "styled-components";
import{NavLink} from "react-router-dom"

const Item = ({ itemId, item }) => {
  // this is one item in the item list
// item to display each item

  return (
  <Wrapper>
    <NavLink to={`/products/${itemId}`}>
      { item &&
      <div> 
      <img src={item.imageSrc}/>
      {item.name}
      {item.price}
      </div>
      }
    </NavLink>
  </Wrapper>

  );
};

const Wrapper = styled.div`

`;



export default Item;
