import styled from "styled-components";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";

const Header = () => {
  const [typedValue, setTypedValue] = useState("");
  const [error, setError] = useState(null);
  const [companyArray, setCompanyArray] = useState([]);
  const companies = [];
  const [brandArray, setBrandArray] = useState([]);
  const brands = [];

  //randomly give 5 companies to be displayed for the brands dropdown
  const getRandomNames = () => {
    const random = [];
    random.push(companies[Math.floor(Math.random() * companies.length)]);
    random.push(companies[Math.floor(Math.random() * companies.length)]);
    random.push(companies[Math.floor(Math.random() * companies.length)]);
    random.push(companies[Math.floor(Math.random() * companies.length)]);
    random.push(companies[Math.floor(Math.random() * companies.length)]);
    random.forEach((element) => {
      const item = (
        <NavLinks key={element.id} to={`/brands/${element.id}`}>
          {element.name}
        </NavLinks>
      );
      setCompanyArray((arr) => [...arr, item]);
    });
  };
  const categoryDisplay = () => {
    brands.forEach((element) => {
      const newElement = element.toLowerCase();
      const item = (
        <NavLinks to={`/categories/${newElement}`}>{element}</NavLinks>
      );
      setBrandArray((arr) => [...arr, item]);
    });
  };
  //this will get all of the names and id of the companies, then store it in the companies array
  // then do the same for the products but creates an array with categories
  useEffect(() => {
    const brand = () => {
      fetch("/api/get-companies")
        .then((res) => res.json())
        .then((data) => {
          data.data.map((company) => {
            const info = {
              name: company.name,
              id: company._id,
            };
            companies.push(info);
          });
        })
        .catch((err) => setError(err))
        .finally(() => getRandomNames());
    };
    const item = () => {
      fetch("/api/get-items")
        .then((res) => res.json())
        .then((data) => {
          data.data.map((product) => {
            if (!brands.includes(product.category)) {
              brands.push(product.category);
            }
          });
        })
        .catch((err) => setError(err))
        .finally(() => categoryDisplay());
    };
    brand();
    item();
  }, []);

  //similar to the other use effect but now its finding the unique categories from items
  // useEffect(() => {
  //     fetch('/api/get-items')
  //     .then(res => res.json())
  //     .then(data => {
  //         console.log(data)
  //         })
  //     })
  //     .catch(err => setError(err))
  //     // .finally(() => getRandomNames())
  // }, [])

  // gets the value of what the user is typing in the search bar
  const getValue = (event) => {
    setTypedValue(event.target.value);
  };

  if (error) {
    return <Wrapper>Error!!!!</Wrapper>;
  }
  return (
    <Wrapper>
      <Top>
        <h1>ADD LOGO</h1>
        <input
          type="text"
          placeholder="Search (icon)"
          value={typedValue}
          onChange={getValue}
        />
        <div className="topLinkDiv">
          <TopLinks className="signin" to="/signin">
            Sign in
          </TopLinks>
          <TopLinks to="/cart">Add icon</TopLinks>
          <TopLinks to="/wishlist">Add icon</TopLinks>
        </div>
      </Top>
      <Nav>
        <NavLinks to="/">Home</NavLinks>
        <NavLinks to="/products">Products</NavLinks>
        <Dropdown>
          <button className="toggle">Brands</button>
          <div className="dropdownMenu">
            <div className="links">
              {/* this will display 5 random brands */}
              {companyArray.map((company) => {
                return company;
              })}
            </div>
          </div>
        </Dropdown>
        <Dropdown>
          <button className="toggle">Categories</button>
          <div className="dropdownMenu">
            <div className="links">
              {brandArray.map((brand) => {
                return brand;
              })}
            </div>
          </div>
        </Dropdown>
      </Nav>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  font-size: 1.3vw;
`;
const Top = styled.div`
  padding-top: 2vw;
  padding-left: 40px;
  width: 90%;
  margin: auto;
  height: 8vw;
  align-items: baseline;
  display: grid;
  grid-template-columns: 0.5fr 0.8fr 0.8fr;
  gap: 2vw;
  font-size: 1.4vw;
  max-width: var(--max-page-width);

  input {
    height: 2.3vw;
    padding-left: 1vw;
    font-size: 1.4vw;
    border-radius: var(--border-radius);
    border: var(--border);
    margin-right: 2vw;
  }
  input:focus {
    outline: none;
  }

  .topLinkDiv {
    margin-left: 1vw;
    padding-left: 1vw;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    gap: 1vw;
  }
  .signin {
    border-right: var(--border);
    padding-left: 15px;
  }
`;
const TopLinks = styled(Link)`
  color: var(--primary-colour);
  text-decoration: none;
`;
const Nav = styled.div`
  padding-top: 2vw;
  width: 90%;
  height: 3vw;
  margin: auto;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr;
  border-top: var(--border);
  max-width: var(--max-page-width);
`;
const NavLinks = styled(Link)`
  color: var(--primary-colour);
  text-decoration: none;
`;
const Dropdown = styled.div`
  position: relative;
  display: inline-block;
  .toggle {
    border: none;
    background-color: white;
    font-size: 1.3vw;
    color: var(--primary-colour);
    font-family: var(--primary-family);
  }
  .dropdownMenu {
    display: none;
    position: absolute;
    z-index: 1;
    width: 20vw;
    border: var(--border);
    border-top: none;
    padding: 1.2vw;
    margin-left: -5vw;
    background-color: white;
  }
  .links {
    display: grid;
  }
  &&:hover .dropdownMenu {
    display: block;
  }
`;

export default Header;
