import styled from "styled-components";
import { Link } from "react-router-dom";
import { useEffect, useState } from "react";

const Header = () => {
    const [typedValue, setTypedValue] = useState('')
    const [error, setError] = useState(null)
    const [companyArray, setCompanyArray] = useState([])
    const companies = [];
    const [brandArray, setBrandArray] = useState([])

    const getRandomNames = () => {
        const random = [];
        random.push(companies[Math.floor])
    }

    useEffect(() => {
        fetch('/api/get-companies')
        .then(res => res.json())
        .then(data => {
            data.data.map(company =>{
                const info = {
                    name: company.name,
                    id: company._id
                }
                companies.push(info);
            })
        })
        .catch(err => setError(err))
        .finally(() => getRandomNames())
    }, [])

    // gets the value of what the user is typing in the search bar 
    const getValue = (event) => {
        setTypedValue(event.target.value);
    }

    if(error){
        return(
            <Wrapper>
                Error!!!!
            </Wrapper>
        )
    }
    return(
        <Wrapper>
            <Top>
                <h1>ADD LOGO</h1>
                <input type="text" placeholder="Search (icon)" value={typedValue} onChange={getValue}/>
                <div className="topLinkDiv">
                    <TopLinks className="signin" to='/signin'>Sign in</TopLinks>
                    <TopLinks to='/cart'>Add icon</TopLinks>
                    <TopLinks to='/wishlist'>Add icon</TopLinks>
                </div>
            </Top>
            <Nav>
                <NavLinks to="/" >Home</NavLinks>
                <NavLinks to='/products'>Products</NavLinks>
                <Dropdown>
                    <button className="toggle">Brands</button>
                    <div className="dropdownMenu">
                        <div className="links">
                            <NavLinks to='/'>Brand</NavLinks>
                        </div>
                    </div>
                </Dropdown>
                <Dropdown>
                    <button className="toggle">Categories</button>
                    <div className="dropdownMenu">
                        <div className="links">
                            <NavLinks to='/'>category</NavLinks>
                            <NavLinks to='/'>category</NavLinks>
                        </div>
                    </div>
                </Dropdown>
            </Nav>
        </Wrapper>
    );
}

const Wrapper = styled.div`
    font-size: 1.3vw;
`;
const Top = styled.div`
    padding-top: 2vw;
    padding-left: 40px;
    width: 90%;
    margin: auto;
    height: 8vw;
    align-items: baseline;
    display: grid;
    grid-template-columns: 0.5fr .8fr .8fr ;
    gap: 2vw;
    font-size: 1.4vw;

    input{
        height: 2.3vw;
        padding-left: 1vw;
        font-size: 1.4vw;
        border-radius: var(--border-radius);
        border: var(--border);
        margin-right: 2vw;
    }
    input:focus{
        outline: none;
    }

    .topLinkDiv{
        margin-left: 1vw;
        padding-left: 1vw;
        display: grid;
        grid-template-columns: 1fr 1fr 1fr ;
        gap: 1vw;
    }
    .signin{
        border-right: var(--border);
        padding-left: 15px;
    }
`;
const TopLinks = styled(Link)`
    color: var(--primary-colour);
    text-decoration: none;

`;
const Nav = styled.div`
    padding-top: 2vw;
    width: 90%;
    height: 3vw;
    margin: auto;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr;
    border-top: var(--border);
`;
const NavLinks = styled(Link)`
    color: var(--primary-colour);
    text-decoration: none;
`;
const Dropdown = styled.div`
    position: relative;
    display: inline-block;
    .toggle{
        border: none;
        background-color: white;
        font-size: 1.3vw;
        color: var(--primary-colour);
        font-family: var(--primary-family);
    }
    .dropdownMenu{
        display: none;
        position: absolute;
        z-index: 1;
        width: 20vw;
        border: var(--border);
        border-top: none;
        padding: 1.2vw;
        margin-left: -5vw;
        background-color: white;
    }
    .links{
        display: grid;
    }
    &&:hover .dropdownMenu{
        display: block;
    }
`;

export default Header;
