import styled from "styled-components";
import Item from "./Item"
import { useContext } from "react"
import { UserActionContext } from "../UserActionContext";
import ActionBar from "./ActionBar";
import { UserContext } from "../UserContext";
import { useEffect } from "react";

const Cart = () => {
    const {cartDetails, getItemDetails, setCartDetails} = useContext(UserActionContext);
    const {cart, isItemsUpdated} = useContext(UserContext);

    // To calculate subtotal
    const getSubTotal = () => {
        if(!cart) return;
        const arrPrice = cart.map(item => item.totalPrice)
        return arrPrice.reduce((a, b) => a+b, 0)
    }

    console.log(cart)
    console.log(cartDetails)
    console.log({isItemsUpdated})

    // This is to render the page and update list items
    useEffect (() => {
        isItemsUpdated && getItemDetails(cart, setCartDetails)
    }, [isItemsUpdated])

    return(
        <Wrapper>
            <>
            <h1>My Bag</h1>
            {cart && 
            <b>Subtotal ({cart.length} items): ${getSubTotal()}</b>
            }
            </>
            {cartDetails && cartDetails.map(item => 
            <ItemWrapper>
                <Item key={`cart-${item._id}`} item={item} />
                <ActionBar item={item} />
            </ItemWrapper>

            )}
        </Wrapper>
    );
}

export default Cart;

const Wrapper = styled.div`
`
const ItemWrapper = styled.div`
    display: flex;
    flex-flow: row nowrap;
`
