import styled from "styled-components";
import { useParams } from "react-router-dom";

const ProductDetails = () => {
  // fetch data to insert details of item
  const { item } = useParams();

  return (
    <Wrapper>
      <Section>
        <ImgSection>
          <img src="" alt="" />
        </ImgSection>

        <DetailSection>
          <div>
            <ProductName>Product Name</ProductName>

            <Description>
              description: Mollit esse duis sunt Lorem laborum commodo sint
              proident mollit magna.
            </Description>

            <Price>Price</Price>
          </div>

          <ButtonSection>
            <button>Add to cart</button>
            <button>heart icon</button>
          </ButtonSection>
        </DetailSection>
      </Section>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  font-size: 20px;
  line-height: 1.5rem;
  padding-top: 50px;
  width: 90%;
  margin: auto;
`;

const Section = styled.div`
  display: flex;
  justify-content: center;
  align-items: flex-start;
  max-width: var(--max-content-width);
  margin: auto;
`;

const ImgSection = styled.div`
  border: var(--border);
  width: 40%;
  width: 250px;
  height: 250px;
  margin-right: 30px;
`;

const DetailSection = styled.section`
  width: 80%;
`;

const ProductName = styled.h2`
  font-size: 1.5rem;
`;

const Description = styled.p`
  margin-top: 30px;
  font-size: 1rem;
`;

const Price = styled.p`
  margin-top: 30px;
  font-size: 1.3rem;
`;

const ButtonSection = styled.section`
  display: flex;
  margin-top: 30px;
`;

export default ProductDetails;
