import { useEffect, useState } from "react";
import styled from "styled-components";
import ItemsDisplay from "../shared/ItemsDisplay";
import AddCarousel from "./AddCarousel";

const Home = () => {
  const [productArr, setProductArr] = useState();
  const [error, setError] = useState();
  const array = [];

  const handleProducts = () => {
    setProductArr(array);
  }


  useEffect(() => {
    fetch('/api/get-items')
    .then(res => res.json())
    .then(data => {
      data.data.map(product => {
        const item = {
          _id:product._id,
          name:product.name,
          imageSrc:product.imageSrc,
          price:product.price,
        }
        array.push(item);
      })
    })
    .catch(err => setError(err))
    .finally(() => handleProducts())
  }, []);

  const getBestSellers = () => {

  }

  if(error){
    return(
      <Wrapper>
        Error
      </Wrapper>
    )
  }
  return productArr? (
    <Wrapper>
      <AddCarousel />
      <Section>
        <h2>- Best Sellers -</h2>
        {getBestSellers()}
      </Section>
      <Section>
        <h2>- New Collection -</h2>
        <ItemsDisplay array={array} />
      </Section>
      <Section>
        <h2>- 30% Off -</h2>
        <ItemsDisplay array={array} />
      </Section>
      <Section>
        <h2>- Picked for you -</h2>
        <ItemsDisplay array={array} />
      </Section>
      <Section>
        <h2>- Back in Stock -</h2>
        <ItemsDisplay array={array} />
      </Section>
    </Wrapper>
  ):(
    <Wrapper>
      loading...
    </Wrapper>
  )
};

const Wrapper = styled.div`
  font-size: 20px;
  padding-top: 50px;
  width: 90%;
  margin: auto;
  max-width: var(--max-page-width);
`;
const Section = styled.div`
  padding-top: 40px;

  h2 {
    text-align: center;
  }
`;
export default Home;
