import styled from "styled-components";
import Slider from "react-slick";
// import { Carousel } from 'react-responsive-carousel';
// import "react-responsive-carousel/lib/styles/carousel.min.css"; // requires a loader


const AddCarousel = () => {
// https://www.npmjs.com/package/react-slick

    const settings = {
        // infinite: true,
        // slidesToShow: 1, // how many are shown 
        // autoplay: true, // making it switch on its own
        // autoplaySpeed: 4000, // how quicly is changes
        dots: true,
      infinite: true,
      slidesToShow: 3,
      slidesToScroll: 1,
      autoplay: true,
      speed: 2000,
      autoplaySpeed: 2000,
      cssEase: "linear"


    }
    return (
        <Wrapper>
            <Carousel {...settings}>
                <div>
                    <h2>IMAGE</h2>
                </div>
                <div>
                    <h2>IMAGE</h2>
                </div>
                <div>
                    <h2>IMAGE</h2>
                </div>
                <div>
                    <h2>IMAGE</h2>
                </div>
                <div>
                    <h2>IMAGE</h2>
                </div>
                <div>
                    <h2>IMAGE</h2>
                </div>
            </Carousel>
        </Wrapper>
    );
}
const Wrapper = styled.div`
    width: 90%;
    height: 30vw;
    border: var(--border);
    margin: auto;
`;

const Carousel = styled(Slider)`
    display: grid;
    grid-template-columns: 1fr 1fr;
`;


export default AddCarousel;
