import styled from "styled-components";
import Item from "./Item"
import { useContext, useState } from "react"
import { UserActionContext } from "../UserActionContext";

import { UserContext } from "../UserContext";
import { useEffect } from "react";
import CheckoutForm from "./CheckoutForm";

const Cart = () => {
    const {cartDetails, getItemDetails, setCartDetails, setIsErr, userId } = useContext(UserActionContext);
    const {cart, isItemsUpdated} = useContext(UserContext);
    const [checkout, setCheckout] = useState(false);
    const [formData, setFormData] = useState({address: {}, payment: {}});
    const [orderNumber, setOrderNumber] = useState(null);
    const [orderErrMsg, setOrderErrMsg] = useState(null);

    // To calculate subtotal
    const getSubTotal = () => {
        if(!cart) return;
        const arrPrice = cart.map(item => item.totalPrice)
        return arrPrice.reduce((a, b) => a+b, 0)
    }

    console.log(cart)
    console.log(cartDetails)
    console.log({isItemsUpdated})

    // This is to render the page and update list items
    useEffect (() => {
        isItemsUpdated && getItemDetails(cart, setCartDetails)
    }, [isItemsUpdated])

    const handleChange = (name, value) => {
        let addressKey = null;
        let paymentKey = null;
        if(name.includes('address.')) {
        addressKey = name.replace('address.', '') 
        setFormData({
            ...formData,
            address: {...formData.address, [addressKey]:value}
        })
        } else if(name.includes('payment.')){
        paymentKey = name.replace('payment.', '') 
        setFormData({
            ...formData,
            payment: {...formData.payment, [paymentKey]:value}
        })
        }
    }

    console.log('formData', formData)

    // To closeModal and reset formData
    const closeModal = () => {
        setCheckout(false)
        setFormData({})
    }

    // This is to submit an order
    const placeOrder = async (e) => {
        e.preventDefault()
        // Add userId to formData
        // formData['_id'] = userId
        formData['_id'] = '460d7e55-fab0-48de-9c07-f61b222bfab6'
        console.log(formData)
        try {
            const res = await fetch('/api/add-order', {
                method: 'POST',
                body: JSON.stringify(formData),
                headers: {
                    'Content-Type': 'application/json',
                    'Accept': 'application/json'
                    }
                })
            const data = await res.json();
            console.log(data)
            if(data.status === 200) {     
                setOrderNumber(data.orderId) // <========== With this state, we can display a confirmation message
            } else {
                setOrderErrMsg(data.message) // <========== Will display an error message e.g. out of stock etc
            }
        } catch (err) {
            setIsErr(true)
        }
    }

    return cart && (
        <Wrapper>
            <div>
                <h1>My Bag ({cart.length} items)</h1>
                {cartDetails && cartDetails.map(item => 
                <ItemWrapper>
                    <Item key={`cart-${item._id}`} item={item} />
                </ItemWrapper>
                )}
            </div>
            <OrderSummary>
                <h1>Order Summary</h1>
                <div>
                    <p>Subtotal: ${getSubTotal()}</p>
                    <p>Shipping: FREE</p>
                    <p>Tax: Calculated at checkout</p>
                    <p>Estimated Total: ${getSubTotal()}</p>
                    <button onClick={() => setCheckout(true)}>CHECKOUT</button>
                </div>
            <CheckoutForm open={checkout} closeModal={closeModal} handleChange={handleChange} formData={formData} placeOrder={placeOrder}/>
            </OrderSummary>
        </Wrapper>
    );
}

export default Cart;

const Wrapper = styled.div`
    font-size: 1vw;
    padding-top: 2vw;
    width: 90%;
    max-width: var(--max-page-width);
    margin: auto;
    display: grid;
    grid-template-columns: 2fr 1fr;

    h1{
        font-size: 1.3vw;
        padding-bottom:
    }
`
const ItemWrapper = styled.div`
    border: var(--border);
    height: 13vw;
`
const OrderSummary = styled.div`

`
