  }
`;

const ItemWrapper = styled.div`
  width: 90%;
  padding-top: 20px;
  margin: auto;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
  gap: 15px;

  div {
    border: var(--border);
    height: 15vw;
  }
`;

export default Category;
