import styled from "styled-components";
import ItemsDisplay from "../shared/ItemsDisplay";
import Banner from "../shared/Banner";
import { useEffect, useState } from "react";


const Brands = () => {
  //set state to render the products
  const [error, setError] = useState(null);
  //set the error state
  const [itemProducts, setItemProducts] =useState(null)
  const array = [];
  const urlPath = window.location.pathname.split('/');
  const brandId = urlPath[2];

  const displayProducts = () => {
    setItemProducts(array);
  }
//fetch all products for Item to render "/api/get-items"
  useEffect(() => {
    fetch("/api/get-items")
      .then((res) => res.json())
      .then((data) => {
        data.data.map((product) => {
          if(product.companyId == brandId){
            const item = {
              _id: product._id,
              name: product.name,
              imageSrc: product.imageSrc,
              price: product.price
            }
            array.push(item)
          }
        })
      })
      .catch((err) => setError(err))
      .finally(() => displayProducts());
  }, []);

  if (error) {
    return <Wrapper>Error!!!!</Wrapper>;
  }
  return itemProducts?(
    <Wrapper>
      <Banner />
      <Section>
        <h2>Brand Name</h2>
        <h2>Brand url</h2>
        <h2>Brand country</h2>
        <ItemWrapper>
          {}
        </ItemWrapper>
      </Section>
    </Wrapper>
  ):(
    <Wrapper>
      loading...
    </Wrapper>
  )
};

const Wrapper = styled.div`
  font-size: 20px;
  padding-top: 50px;
  width: 90%;
  margin: auto;
  max-width: var(--max-page-width);
`;
const Section = styled.div`
  padding-top: 40px;
  max-width: var(--max-content-widt);

  h2 {
    text-align: left;
    width: 90%;
    margin: auto;
  }
`;

const ItemWrapper = styled.div`
  width: 90%;
  padding-top: 20px;
  margin: auto;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
  gap: 15px;

  div {
    border: var(--border);
    height: 15vw;
  }
`;

export default Brands;
