import styled from "styled-components"
import {useState} from "react"
import { useNavigate } from "react-router-dom";
import Signin from "./Signin";

const Signup = () => {

    //setting the form state 
const [firstName, setFirstName] = useState('');
const [lastName, setLastName] = useState('');
const [email, setEmail] = useState('');
const [error, setError] =useState(false)

const navigate = useNavigate()

// handle each element inside the form 
const handleFirsName = (e) => {
    setFirstName(e.target.value);   
}
const handleLastName = (e) => {
    setLastName(e.target.value);
}
const handleEmail = (e) => {
    setEmail(e.target.value); 
}
// sending information to the backend 
    const handleSubmit = (e) => {
        e.preventDefault(); 

        fetch("/api/add-user", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
              Accept: "application/json",
            },
            body: JSON.stringify({firstName, lastName, email}),
        })
        .then(res=> res.json())
        .then(data=> {
            window.localStorage.setItem("user-id", data.userId)
            navigate("/")

        })
        .catch(err=> setError(err))

    }

    if(error) {
        return (
            <Wrapper>Error!!</Wrapper>
        )
    }


    return (
        <>
        <Wrapper>
            <form onSubmit={(e)=> handleSubmit(e)}>
                <div>
                    <label className="fisrtNameLable">First Name: </label>
                    <label className="lastNameLable">Last Name: </label>
                </div>
                <div>
                    <input onChange={handleFirsName} value={firstName} className="firstName" type='text' required={true}></input>
                    <input onChange={handleLastName} value={lastName} className="lastName" type='text' required={true}></input>
                </div>
                <div>
                    <label>Email: </label>
                </div>
                <div>
                    <input onChange={handleEmail} value={email} type='email' required={true}></input>
                </div>
                <div>
                    <label>Password: </label>
                </div>
                <div>
                    <input type='password'></input>
                </div>
                <div>
                    <button type="submit" >Sign Up</button>  
                </div>
            </form>
        </Wrapper>
        {/* <Signin isSubmit={isSubmit}/> */}
        </>
    );
}


const Wrapper = styled.div`
    form{
        border-top: var(--border);
        border-bottom: var(--border);
    }
    input{
        width: 9vw;
        padding-left: .5vw;
        height: 1.2vw;
        border: var(--border);
        border-radius: var(--border-radius);
    }
    div{
        width: 80%;
        padding-top: 0.5vw;
        padding-bottom: 0.5vw;
    }
    .lastName{
        margin-left: 1.5vw;
    }
    .lastNameLable{
        padding-left: 4.7vw;
    }
`;
export default Signup
