import styled from "styled-components";
import { Link } from "react-router-dom";
import { useState } from "react";

const Header = () => {
    const [typedValue, setTypedValue] = useState('');
    const [error, setError] = useState(null);
    //const [companyArray, setCompanyArray] = useState('');
    const getCompanies = () => {
        fetch("/api/get-items")
        .then(res => res.json())
        .then(data => {
            console.log(data)
        })
        .catch(err => setError(err))
    }
    // gets the value of what the user is typing in the search bar 
    const getValue = (event) => {
        setTypedValue(event.target.value);

    }
    getCompanies();
    return(
        <Wrapper>
            <Top>
                <h1>ADD LOGO</h1>
                <input type="text" placeholder="Search (icon)" value={typedValue} onChange={getValue}/>
                <div className="topLinkDiv">
                    <TopLinks className="signin" to='/signin'>Sign in</TopLinks>
                    <TopLinks to='/cart'>Add icon</TopLinks>
                    <TopLinks to='/wishlist'>Add icon</TopLinks>
                </div>
            </Top>
            <Nav>
                <NavLinks to="/" >Home</NavLinks>
                <NavLinks to="/products" >Products</NavLinks>
                <Dropdown>
                    <div className="dropdown">
                        <button className="dropdownToggle">Categories</button>
                        <div className="dropdownMenu">
                            <div className="links">
                                <NavLinks to="/" >category</NavLinks>
                                <NavLinks to="/" >category</NavLinks>
                                <NavLinks to="/" >category</NavLinks>
                            </div>
                        </div>
                    </div>
                </Dropdown>
                <Dropdown>
                    <div className="dropdown">
                        <button className="dropdownToggle">Brands</button>
                        <div className="dropdownMenu">
                            <div className="links">
                                <NavLinks to="/" >brand</NavLinks>
                                <NavLinks to="/" >brand</NavLinks>
                                <NavLinks to="/" >brand</NavLinks>
                            </div>
                        </div>
                    </div>
                </Dropdown>
            </Nav>
        </Wrapper>
    );
}

const Wrapper = styled.div`
font-size: 1.3vw;
`;
const Top = styled.div`
    padding-top: 2vw;
    padding-left: 40px;
    width: 90%;
    margin: auto;
    height: 8vw;
    align-items: baseline;
    display: grid;
    grid-template-columns: 0.5fr .8fr 0.8fr ;
    gap: 5vw;


    input{
        height: 3vw;
        padding-left: 1vw;
        font-size: 1.4vw;
        border-radius: var(--border-radius);
        border: var(--border);
        margin-right: 5vw;
    }
    input:focus{
        outline: none;
    }

    .topLinkDiv{
        //width: 40%;
        margin-left: 2vw;
        padding-left: 10px;
        display: grid;
        grid-template-columns: 1fr 1fr 1fr 1fr;
        gap: 2vw;
    }
    .signin{
        border-right: var(--border);
        padding-left: 15px;
    }
`;
const TopLinks = styled(Link)`
    color: var(--primary-colour);
    text-decoration: none;

`;
const Nav = styled.div`
    padding-top: 2vw;
    width: 90%;
    height: 2.6vw;
    margin: auto;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr 1fr;
    border-top: var(--border);
`;
const NavLinks = styled(Link)`
    color: var(--primary-colour);
    text-decoration: none;
`;
const Dropdown = styled.div`
    position: relative;
    display: inline-block;
    .dropdownMenu{
        display: none;
        position: absolute;
        width: 20vw;
        margin-left: -5vw;
        z-index: 1;
        padding: 1.5vw;
        border: var(--border);
        border-top: none;
        background-color: white;
    }
    .links{
        display: grid;
    }
    .dropdown:hover .dropdownMenu{
        display: block;
    }
    .dropdownToggle{
        border: none;
        background-color: white;
        font-size: 1.3vw;
        font-family: var(--primary-family);
        color: var(--primary-colour);
    }


`;


export default Header;
