import { useEffect, useState } from "react";
import DisplayError from "./shared/DisplayError";
import styled from "styled-components";
import { Link } from "react-router-dom";

const SearchBar = () => {
    const [suggestions, setSuggestions] = useState();
    const [typedValue, setTypedValue] = useState("");
    const [itemsArr, setItemArr] = useState();
    const [error, setError] = useState();
    const  array = [];

    useEffect(() => {
        const getItems = () => {
            fetch("/api/get-items")
            .then((res) => res.json())
            .then((data) => {
                data.data.map((product) => {
                    const item = {
                        _id: product._id,
                        name: product.name,
                    };
                    array.push(item);
                });
            })
            .catch((err) => setError(err))
            .finally(() => setItemArr(array));
        }
        getItems();
    }, [])

    const searchMatches = () => {
        if (itemsArr) {
            const matches = []
            itemsArr.map(item => {
                const name = item.name.toLowerCase();
                if ((name.search(typedValue)) !== -1){
                    matches.push(<Suggestion to={`/products/${item._id}`}>{item.name}</Suggestion>)
                }
            })
            setSuggestions(matches);
        }
    }

    useEffect(() => {
            searchMatches();
    }, [typedValue])

    // gets the value of what the user is typing in the search bar
    const getValue = (event) => {
        setTypedValue(event.target.value);
    };

    if (error) {
        return (<Wrapper>
            <DisplayError/>
            </Wrapper>);
        }
    return(
        <Wrapper>
            <input
            type="text"
            placeholder="🔍 Search"
            value={typedValue}
            onChange={getValue}
        />
            {suggestions? (
                <Suggestion>
                    {suggestions.map(element => {
                        return element;
                    })}
                </Suggestion>
            ):(
                <NoSuggestion>
                </NoSuggestion>
            )}
        </Wrapper>

    )
}
const Wrapper = styled.div` 
    input {
        width: 25vw;
        height: 2.3vw;
        padding-left: 1vw;
        font-size: 1.2vw;
        border-radius: var(--border-radius);
        border: var(--border);
    }

    input:focus {
        outline: none;
    }
`;

const Suggestion = styled(Link)`

`;

const NoSuggestion = styled.div`
    display: block;
`;
export default SearchBar;
