import { useEffect, useState } from "react";
import styled from "styled-components";
import ItemsDisplay from "../shared/ItemsDisplay";
import AddCarousel from "./AddCarousel";

const Home = () => {
  const [productArr, setProductArr] = useState()
  const array = [];

  useEffect(() => {
    fetch('/api/get-items')
    .then(res => res.json())
    .the
  }, [])

  return (
    <Wrapper>
      <AddCarousel />
      <Section>
        <h2>- Best Sellers -</h2>
        <ItemsDisplay array={array} />
      </Section>
      <Section>
        <h2>- New Collection -</h2>
        <ItemsDisplay array={array} />
      </Section>
      <Section>
        <h2>- 30% Off -</h2>
        <ItemsDisplay array={array} />
      </Section>
      <Section>
        <h2>- Picked for you -</h2>
        <ItemsDisplay array={array} />
      </Section>
      <Section>
        <h2>- Back in Stock -</h2>
        <ItemsDisplay array={array} />
      </Section>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  font-size: 20px;
  padding-top: 50px;
  width: 90%;
  margin: auto;
  max-width: var(--max-page-width);
`;
const Section = styled.div`
  padding-top: 40px;

  h2 {
    text-align: center;
  }
`;
export default Home;
