import styled from "styled-components";
import { useEffect, useState } from "react";
import Item from "../shared/Item";

const Category = () => {
  //set state to render the products
  //set the error state
  const [error, setError] = useState(null);
  // sets the caregory name
  const [categoryName, setCategoryName] = useState(null);
  const array = [];
  const urlPath = window.location.pathname.split('/');
  const categoryId = urlPath[2];
  const displayProducts = () => {
    setCategoryName(array);
    }
  //fetch all products for Category to render
    useEffect(() => {
      fetch("/api/get-items")
      .then((res) => res.json())
      .then((data) => {
        data.data.map((product) => {
          if(product.category.toLowerCase() == categoryId){
            const item = {
              _id: product._id,
              name: product.name,
              imageSrc: product.imageSrc,
              price: product.price
            }
            array.push(item)
          }
        })
      })
      .catch((err) => setError(err))
      .finally(() => displayProducts());
    },[])

    if (error) {
      return <Wrapper>Error!!!!</Wrapper>;
    }
  return (
    <Wrapper>
      <Section>
        <h2>- Category Name -</h2>

        <ItemWrapper>
          <Item array={array} />
        </ItemWrapper>
      </Section>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  font-size: 20px;
  padding-top: 50px;
  width: 90%;
  margin: auto;
  max-width: var(--max-page-width);
`;
const Section = styled.div`
  padding-top: 40px;
  max-width: var(--max-content-widt);

  h2 {
    text-align: center;
  }
`;

const ItemWrapper = styled.div`
  width: 90%;
  padding-top: 20px;
  margin: auto;
  display: grid;
  grid-template-columns: 1fr 1fr 1fr 1fr 1fr;
  gap: 15px;

  div {
    border: var(--border);
    height: 15vw;
  }
`;

export default Category;
