import styled from "styled-components";

const Account = () => {
  return (
    <Wrapper>
      <Section>
        <LeftSection>Icon here</LeftSection>

        <RightSection>
          <div>
            <UserName>Name of user</UserName>
            <h4>shipping info</h4>
            <p>My Address</p>
            <p>My E-mail</p>
            <div>some info here...</div>
          </div>

          <ButtonSection>
            <Button>My orders</Button>
            <Button>My Returns</Button>
            <Button>My Payments</Button>
            <Button>Wishlist</Button>
            <Button>Buy it again</Button>
            <Button>Account setting</Button>
          </ButtonSection>

          <SignOut type="">Sign out</SignOut>
        </RightSection>
      </Section>
    </Wrapper>
  );
};

const Wrapper = styled.div`
  font-size: 20px;
  line-height: 1.5rem;
  padding-top: 50px;
  width: 90%;
  margin: auto;
`;
const Section = styled.div`
  display: flex;
  justify-content: center;
  align-items: flex-start;
  max-width: var(--max-width);
  margin: auto;
`;

const LeftSection = styled.div`
  width: 10%;
  width: 100px;
  height: 100px;
  border: var(--border);
  margin-right: 30px;
`;
const UserName = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 20px;
`;

const RightSection = styled.div`
  width: 90%;
`;

const ButtonSection = styled.div`
  display: flex;
  flex-wrap: wrap;
  justify-content: space-between;
  gap: 15px;
  margin-top: 20px;
`;
const Button = styled.button`
  flex-grow: 1;
  width: 30%;
  height: 50px;
  border-radius: 5px;
`;
const SignOut = styled.button`
  width: 100%;
  height: 40px;
  margin-top: 20px;
  border-radius: 5px;
`;

export default Account;
