import styled from "styled-components";
import { Link } from "react-router-dom";

const Footer = () => {
    return(
        <Wrapper>
            <div className="topFooter">
                <div className="col1">
                    <p>About</p>
                    <p>Products</p>
                    <p>Contact Us</p>
                    <p>FAQ</p>
                    <p>Careers</p>
                </div>
                <div className="col2">
                    <p>My Account</p>
                    <p>Edit Information</p>
                    <p>Orders</p>
                    <p>Returns & Exchages</p>

                </div>
                <div className="col3">
                    <p>Sign up for Newsletters: </p>
                    <form>
                        <input type="text" placeholder="Email:" />
                        <button type="submit">Submit</button>
                    </form>

                </div>
            </div>
            <div className="bottomFooter">
                <div className="other">
                    <p></p>
                </div>
                <div className="socials">

                </div>
            </div>

        </Wrapper>
    );
}
const Wrapper = styled.div`
    padding-top: 3vw;
    width: 90%;
    margin: auto;
    font-size: 1.2vw;
    .topFooter{
        border-top: var(--border);
        padding-top: 3vw;
        height: 10vw;
        display: grid;
        grid-template-columns: 1fr 1fr 2fr;
    }
    .col3{
        font-size: 1.5vw;
        padding-left: 5vw;

    }
    input{
        height: 2vw;
        padding-left: 1vw;
        font-size: 1.4vw;
        border-radius: var(--border-radius);
        border: var(--border);
        margin-top: 1vw;
    }
    input:focus{
        outline: none;
    }
`;
const topFooter = styled.div`

`;
export default Footer;
